mapper.select:
select count(1)
from article
where article_status = #{status}

select 
from article

    and article_id = #{articleId}
    and article_user_id = #{articleUserId}
    and article_title like concat('%',#{articleTitle},'%')
    and article_content = #{articleContent}
    and article_view_count = #{articleViewCount}
    and article_comment_count = #{articleCommentCount}
    and article_like_count = #{articleLikeCount}
    and article_is_comment = #{articleIsComment}
    and article_status = #{articleStatus}
    and article_order = #{articleOrder}
    and article_update_time = #{articleUpdateTime}
    and article_create_time = #{articleCreateTime}
    and article_summary = #{articleSummary}

ORDER BY `article`.`article_order` DESC, `article`.`article_id` DESC

select article.* from article
and article.article_status = #{status}
and article.article_title LIKE concat(concat('%',#{keywords}),'%')
and article.article_user_id = #{userId}
and article.article_id IN (SELECT article_tag_ref.article_id FROM article_tag_ref WHERE article_tag_ref.tag_id = #{tagId})
and article.article_id IN (SELECT article_category_ref.article_id FROM article_category_ref WHERE article_category_ref.category_id = #{categoryId})

select 
from article
where article_status = 1 order by article_update_time desc limit #{limit};

mapper.update:
UPDATE article
SET article_comment_count = (SELECT count(1) FROM `comment` WHERE comment.comment_article_id = article.article_id)
WHERE article.article_id = #{id}

mapper.delete:
delete from article
where article_id in

    #{id}
